INSERT INTO venue (venue_name, venue_address, venue_description, venue_map) VALUES ('Gasa Gasa', '4920 Freret St, New Orleans, LA 70115', 'Gasa Gasa is the uptown neighborhood''s newest music and arts venue. Located on Feret Street, Gasa Gasa supports local talent through weekly events.', 'https://www.google.com/maps/embed/v1/place?q=place_id:ChIJ4_JiSKmlIIYRTkZm4D0v1zA&key=AIzaSyBRq0tBrXJcI2Tff8JxVYGmR58CimOPI7o');
INSERT INTO venue (venue_name, venue_address, venue_description, venue_map) VALUES ('The Howlin'' Wolf', '907 S Peters St, New Orleans, LA 70130', 'Musical acts shine at this expansive, low-key concert hall with a mahogany bar & pub fare.', 'https://www.google.com/maps/embed/v1/place?q=place_id:ChIJSYn2znGmIIYRmCUMqQhZ_Zc&key=AIzaSyBRq0tBrXJcI2Tff8JxVYGmR58CimOPI7o');
