INSERT INTO venue (venue_name, venue_address, venue_description, venue_map) VALUES ('Fillmore New Orleans', '6 Canal St, New Orleans, LA 70130', 'The Fillmore was the focal point for the psychedelic music scene during the 1960s and 1970s, helping launch the careers of the biggest names of the era such as The Grateful Dead, Jimi Hendrix, Led Zeppelin, and The Doors.', 'https://www.google.com/maps/embed/v1/place?q=place_id:ChIJSQBCQW2mIIYRAkwTxKE9zQk&key=AIzaSyBRq0tBrXJcI2Tff8JxVYGmR58CimOPI7o');
INSERT INTO venue (venue_name, venue_address, venue_description, venue_map) VALUES ('Tipitina''s', '501 Napoleon Ave, New Orleans, LA 70115', 'Rustic, black-and-white tiled warehouse space & landmark since ’77 for live music & Cajun dancing.', 'https://www.google.com/maps/embed/v1/place?q=place_id:ChIJ7Q8OmU-kIIYR6f0q4VwM-Ik&key=AIzaSyBRq0tBrXJcI2Tff8JxVYGmR58CimOPI7o');
INSERT INTO venue (venue_name, venue_address, venue_description, venue_map) VALUES ('Three Keys At Ace Hotel New Orleans', '600 Carondelet St, New Orleans, LA 70130', 'Cocktails and casual eats in the lobby makes for great people watching. Reasonably priced and conveniently located in the CBD.', 'https://www.google.com/maps/embed/v1/place?q=place_id:ChIJHdxs-XSmIIYRKBmpbJoSajI&key=AIzaSyBRq0tBrXJcI2Tff8JxVYGmR58CimOPI7o');
INSERT INTO venue (venue_name, venue_address, venue_description, venue_map) VALUES ('Republic NOLA', '828 S Peters St, New Orleans, LA 70130', 'Warehouse turned concert venue & club (Thursdays–Saturdays) with wooden beams & crystal chandeliers.', 'https://www.google.com/maps/embed/v1/place?q=place_id:ChIJJ9Zs5HGmIIYRldJ5SKhZC90&key=AIzaSyBRq0tBrXJcI2Tff8JxVYGmR58CimOPI7o');
